import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { DotLoader } from 'react-spinners'; // Import DotLoader from react-spinners

function Loader() {
    return (
        <div className="loader-container">
            <DotLoader color="#36D7B7" loading={true} size={50} /> {/* DotLoader component */}
        </div>
    );
}

function App() {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false); // State to manage loading status

    // Function to fetch data from the server
    const fetchData = async () => {
        setLoading(true); // Set loading to true when fetching data
        setError('');
        try {
            const response = await axios.get(`http://localhost:3000/search?q=${encodeURIComponent(query)}`);
            if (!response.data || !Array.isArray(response.data)) {
                throw new Error('Invalid response from server');
            }
            setResults(response.data);
            // Store fetched data in local storage
            localStorage.setItem('searchResults', JSON.stringify(response.data));
        } catch (error) {
            console.error('Error fetching data:', error);
            setError('Error fetching data: ' + error.message);
        } finally {
            setLoading(false); // Set loading to false after fetching data
        }
    };

    // Function to load cached data from local storage
    const loadCachedData = () => {
        const cachedResults = localStorage.getItem('searchResults');
        if (cachedResults) {
            setResults(JSON.parse(cachedResults));
        }
    };

    useEffect(() => {
        // Load cached data when the component mounts
        loadCachedData();
        // Load the saved query text from local storage
        const savedQuery = localStorage.getItem('searchQuery');
        if (savedQuery) {
            setQuery(savedQuery);
        }
    }, []); // Empty dependency array ensures this effect runs only once when the component mounts

    // Function to handle form submission
    const handleSearch = async (event) => {
        event.preventDefault();
        await fetchData(); // Fetch data from the server
        // Store the search query text in local storage
        localStorage.setItem('searchQuery', query);
    };

    // Function to handle changes in the search input
    const handleInputChange = (event) => {
        setQuery(event.target.value);
    };

    return (
        <div className="App">
            <h1>Medium Search</h1>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    value={query}
                    onChange={handleInputChange} // Handle input change
                    placeholder="Enter search term"
                />
                <button type="submit">Search</button>
            </form>
            {loading ? ( // Only display loader if loading is true
                <Loader />
            ) : (
                <div>
                    {error && <p>{error}</p>}
                    {results.length > 0 ? (
                        results.map((result, index) => (
                            <div key={index} className="result">
                                <h3>{result.title}</h3>
                                <p>Author: {result.author}</p>

                                <a href={result.blogUrl}>read blog</a>
                                <br></br>   
                                <a href={result.url} target="_blank" rel="noopener noreferrer">Read more</a>
                            </div>
                        ))
                    ) : (
                        <p>No results found</p>
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
