import React, { useState } from 'react';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async (event) => {
    event.preventDefault();
    const response = await fetch(`http://localhost:3001/search?q=${encodeURIComponent(query)}`);
    const data = await response.json();
    setResults(data);
  };

  return (
    <div>
      <h1>Medium Blog Search</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for Medium blogs"
          required
        />
        <button type="submit">Search</button>
      </form>
      <div>
        {results.map((result, index) => (
          <div key={index}>
            <h3>{result.title}</h3>
            <p>by {result.author} on {new Date(result.date).toLocaleDateString()}</p>
            <a href={result.url} target="_blank" rel="noopener noreferrer">Read more</a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
